set define ødeclare  l_foo number;  l_app_id number;begin  l_foo := adc_admin.map_id;  l_app_id := coalesce(apex_application_install.get_application_id, øAPP_ID.);  adc_admin.prepare_rule_group_import(    p_cgr_app_id => l_app_id,    p_cgr_page_id => 16);  adc_admin.merge_rule_group(    p_cgr_id => adc_admin.map_id(1350),    p_cgr_app_id => l_app_id,    p_cgr_page_id => 16,    p_cgr_with_recursion => adc_util.C_TRUE,    p_cgr_active => adc_util.C_TRUE);    adc_admin.merge_rule(    p_cru_id => adc_admin.map_id(1352),    p_cru_cgr_id => adc_admin.map_id(1350),    p_cru_name => 'die Seite öffnet',    p_cru_condition => q'|initializing = c_true|',    p_cru_sort_seq => 10,    p_cru_fire_on_page_load => adc_util.C_FALSE,    p_cru_active => adc_util.C_TRUE);    adc_admin.merge_rule_action(    p_cra_id => adc_admin.map_id(1354),    p_cra_cru_id => adc_admin.map_id(1352),    p_cra_cgr_id => adc_admin.map_id(1350),    p_cra_cpi_id => 'DOCUMENT',    p_cra_cat_id => 'IS_MANDATORY',    p_cra_param_1 => q'||',    p_cra_param_2 => q'|.adc-mandatory|',    p_cra_param_3 => q'||',    p_cra_sort_seq => 10,    p_cra_on_error => adc_util.C_FALSE,    p_cra_raise_recursive => adc_util.C_TRUE,    p_cra_raise_on_validation => adc_util.C_TRUE,    p_cra_active => adc_util.C_TRUE);  adc_admin.merge_rule(    p_cru_id => adc_admin.map_id(1356),    p_cru_cgr_id => adc_admin.map_id(1350),    p_cru_name => 'eine Seite im Modus COMMISSION öffnet',    p_cru_condition => q'|initializing = c_true and P16_PAGE_MODE = 'COMMISSION'|',    p_cru_sort_seq => 20,    p_cru_fire_on_page_load => adc_util.C_TRUE,    p_cru_active => adc_util.C_TRUE);    adc_admin.merge_rule_action(    p_cra_id => adc_admin.map_id(1358),    p_cra_cru_id => adc_admin.map_id(1356),    p_cra_cgr_id => adc_admin.map_id(1350),    p_cra_cpi_id => 'P16_EMP_COMMISSION_PCT',    p_cra_cat_id => 'IS_MANDATORY',    p_cra_param_1 => q'||',    p_cra_param_2 => q'||',    p_cra_param_3 => q'||',    p_cra_sort_seq => 10,    p_cra_on_error => adc_util.C_FALSE,    p_cra_raise_recursive => adc_util.C_TRUE,    p_cra_raise_on_validation => adc_util.C_TRUE,    p_cra_active => adc_util.C_TRUE);  adc_admin.merge_rule(    p_cru_id => adc_admin.map_id(1360),    p_cru_cgr_id => adc_admin.map_id(1350),    p_cru_name => 'die Seite in einem anderen Modus öffnet',    p_cru_condition => q'|initializing = c_true and coalesce(P16_PAGE_MODE, 'FOO') != 'COMMISSION'|',    p_cru_sort_seq => 30,    p_cru_fire_on_page_load => adc_util.C_TRUE,    p_cru_active => adc_util.C_TRUE);    adc_admin.merge_rule_action(    p_cra_id => adc_admin.map_id(1362),    p_cra_cru_id => adc_admin.map_id(1360),    p_cra_cgr_id => adc_admin.map_id(1350),    p_cra_cpi_id => 'P16_EMP_COMMISSION_PCT',    p_cra_cat_id => 'SET_VISUAL_STATE',    p_cra_param_1 => q'|HIDE|',    p_cra_param_2 => q'||',    p_cra_param_3 => q'||',    p_cra_sort_seq => 10,    p_cra_on_error => adc_util.C_FALSE,    p_cra_raise_recursive => adc_util.C_TRUE,    p_cra_raise_on_validation => adc_util.C_TRUE,    p_cra_active => adc_util.C_TRUE);    adc_admin.propagate_rule_change(adc_admin.map_id(1350));  commit;end;/set define on