set define ~declare  l_foo number;  l_app_id number;begin  l_foo := adc_admin.map_id;  l_app_id := coalesce(apex_application_install.get_application_id, ~APP_ID.);  adc_admin.prepare_rule_group_import(    p_cgr_app_id => l_app_id,    p_cgr_page_id => 17);  adc_admin.merge_rule_group(    p_cgr_id => adc_admin.map_id(1522),    p_cgr_app_id => l_app_id,    p_cgr_page_id => 17,    p_cgr_with_recursion => adc_util.C_TRUE,    p_cgr_active => adc_util.C_TRUE);    adc_admin.merge_rule(    p_cru_id => adc_admin.map_id(1524),    p_cru_cgr_id => adc_admin.map_id(1522),    p_cru_name => 'die Seite öffnet',    p_cru_condition => q'|initializing = c_true|',    p_cru_sort_seq => 10,    p_cru_fire_on_page_load => adc_util.C_FALSE,    p_cru_active => adc_util.C_TRUE);    adc_admin.merge_rule_action(    p_cra_id => adc_admin.map_id(1528),    p_cra_cru_id => adc_admin.map_id(1524),    p_cra_cgr_id => adc_admin.map_id(1522),    p_cra_cpi_id => 'DOCUMENT',    p_cra_cat_id => 'EXECUTE_COMMAND',    p_cra_param_1 => q'|1526|',    p_cra_param_2 => q'||',    p_cra_param_3 => q'||',    p_cra_sort_seq => 10,    p_cra_on_error => adc_util.C_FALSE,    p_cra_raise_recursive => adc_util.C_TRUE,    p_cra_raise_on_validation => adc_util.C_TRUE,    p_cra_active => adc_util.C_TRUE);  adc_admin.merge_rule(    p_cru_id => adc_admin.map_id(1535),    p_cru_cgr_id => adc_admin.map_id(1522),    p_cru_name => 'das Testkommando ausführt',    p_cru_condition => q'|command = 'test-action'|',    p_cru_sort_seq => 20,    p_cru_fire_on_page_load => adc_util.C_FALSE,    p_cru_active => adc_util.C_TRUE);    adc_admin.merge_rule_action(    p_cra_id => adc_admin.map_id(1537),    p_cra_cru_id => adc_admin.map_id(1535),    p_cra_cgr_id => adc_admin.map_id(1522),    p_cra_cpi_id => 'DOCUMENT',    p_cra_cat_id => 'NOTIFY',    p_cra_param_1 => q'|Hallo Welt, ich war ein Seitenkommando.|',    p_cra_param_2 => q'||',    p_cra_param_3 => q'||',    p_cra_sort_seq =>